let widthScreen=0,scrollPage=0;const element=document.querySelector('.conteudo-right');let widthElement=0,distanceElementTop=0;var initialDistanceTop=element.offsetTop;var containerElement=document.querySelector('.container');let rightElement=0;function setPositionElement(element,container){widthScreen=document.body.clientWidth||window.innerWidth;scrollPage=window.pageYOffset;distanceElementTop=element.offsetTop;widthElement=element.offsetWidth+'px';containerElement=container||document.querySelector('.container');rightElement=(widthScreen-containerElement.offsetWidth)/ 2;if(scrollPage>(initialDistanceTop-15)&&widthScreen>980){element.classList.add('fixed');element.style.width=widthElement;element.style.right=rightElement+'px';}else{element.classList.remove('fixed');element.removeAttribute("style");}}window.onresize=function(){setPositionElement(element);};window.onscroll=function(){setPositionElement(element);};window.onload=function(){setPositionElement(element);};
