let widthScreen=0,scrollPage=0;const element=document.querySelector('.conteudo-right');let widthElement=0,distanceElementTop=0;var initialDistanceTop=element.offsetTop;var containerElement=document.querySelector('.container');let rightElement=0;function setPositionElement(element,container){widthScreen=document.body.clientWidth||window.innerWidth;scrollPage=document.documentElement.scrollTop||window.scrollY;distanceElementTop=element.offsetTop;widthElement=element.offsetWidth+'px';containerElement=container||document.querySelector('.container');rightElement=(widthScreen-containerElement.offsetWidth)/2;if(scrollPage>(initialDistanceTop-15)&&widthScreen>980){element.classList.add('fixed');element.style.width=widthElement;element.style.right=rightElement+'px'}else{element.classList.remove('fixed');element.removeAttribute("style")}}
document.getElementsByTagName("body")[0].onresize=function(){setPositionElement(element)};document.getElementsByTagName("body")[0].onscroll=function(){setPositionElement(element)};document.getElementsByTagName("body")[0].onload=function(){setPositionElement(element)}